mapper.select:
SELECT COUNT(1) FROM `event_type` WHERE 1=1
and name LIKE CONCAT('%', #{keyword}, '%')
and parent_id = #{parentId}

SELECT `id`, `name`, `lft`, `rht`, `parent_id` AS `parentId`, `layer` FROM `event_type` where 1=1
and `name` LIKE CONCAT('%', #{keyword}, '%')
and `parent_id` = #{parentId}
ORDER BY `lft`
LIMIT #{startNum}, #{pageSize}

SELECT a.`id`, COUNT(DISTINCT c.`id`) AS childCount FROM `event_type` a LEFT JOIN `event_type` c ON c.`parent_id` = a.`id` WHERE a.`id` IN
#{eventTypeId}
GROUP BY a.`id`

select
b.`event_type_id`,
b.`type`,
b.`uuid`
FROM `event_type` a
LEFT JOIN `event_type_authority` b on a.`id` = b.`event_type_id`
WHERE a.`lft` > #{lft} AND a.`rht` < #{rht}
AND `type` = #{authorityVo.type}
AND `uuid` = #{authorityVo.uuid}

select
distinct(s.`id`) as id,
s.`name`
from `event_solution` s
left join `event_type_solution` ets on s.`id` = ets.`solution_id`
left join `event_type` et on et.`id` = ets.`event_type_id`
where et.`lft` <=#{vo.lft} and et.`rht` >=#{vo.rht}

	and s.`is_active` = #{isActive}


	LIMIT #{vo.startNum}, #{vo.pageSize}

SELECT 
  DISTINCT `event_type_id`
FROM `event_type_authority` 
WHERE (type = 'common' AND `uuid` = 'alluser')

	OR (type = 'user' AND `uuid` = #{userUuid})


	OR (type = 'team' AND `uuid` IN

		#{teamUuid}

	)


	OR (type = 'role' AND `uuid` IN

		#{roleUuid}

	)
